public function setPostLike_post()
{
$this->form_validation->set_rules('auth_token', 'Token', 'required');
$this->form_validation->set_rules('member_id', 'MemberID', 'required');
$this->form_validation->set_rules('post_id', 'PostID', 'required');
$this->form_validation->set_rules('like_status', 'Like Status', 'required');
$this->form_validation->set_message('required', '%s is required');
if ($this->form_validation->run() === FALSE) {
$strip_message = strip_tags(validation_errors(""));
$this->response(array(
"status" => FALSE,
"message" => trim(preg_replace("/\r\n|\r|\n/", ',', $strip_message), ","),
'responseCode' => self::HTTP_BAD_REQUEST,
), REST_Controller::HTTP_BAD_REQUEST);
}
$postId = $this->input->post('post_id');
$MemberId = $this->input->post('member_id');
$likeStatus = $this->input->post("like_status");
$existence = $this->Mdl_post->checkPostId($postId, $this->config->item('PostTable'));
if ($existence['count'] == 0) {
$this->response(array(
"status" => FALSE,
"message" => "No Such Post Exist",
'responseCode' => self::HTTP_BAD_REQUEST,
), REST_Controller::HTTP_BAD_REQUEST);
}
$recordExist = $this->Mdl_post->checkPostId($postId, $this->config->item('LikeTable'), $MemberId);
if ($recordExist['count'] == 0) {
$operation_status = "insert";
$post_like_data = array(
'member_id' => $MemberId,
'post_id' => $postId,
'is_liked' => $likeStatus,
);
} else {
$operation_status = "update";
$post_like_data = array(
'member_id' => $MemberId,
'post_id' => $postId,
'is_liked' => $likeStatus,
);
}
$likeResult = $this->Mdl_post->setPostLike($post_like_data, $operation_status);
if ($likeResult == true) {
$this->response(array(
'status' => TRUE,
'responseCode' => self::HTTP_OK,
'message' => ($post_like_data['is_liked'] == 1) ? 'like' : "unlike",
'no_of_like' => $this->Mdl_post->getNumberOfLikesByPostId($postId),
), REST_Controller::HTTP_OK);
} else {
$this->response(array(
"status" => FALSE,
'responseCode' => self::HTTP_NOT_FOUND,
'message' => "Like Status ain't Changed",
'data' => null,
), REST_Controller::HTTP_NOT_FOUND);
}
}

